#define LDPC_BITS2BYTES(__bits_cnt)	(((__bits_cnt) + 7) >> 3)
#define LDPC_M_IDX(__matr, __row, __col) ((((__row) * (__matr)->cw_size) + (__col)))
#define LDPC_GET_BIT(__buf, __bit)	(((((uint8_t*)(__buf))[((__bit) >> 3)] >> ((__bit) & 0x07))) & 0x01)
#define LDPC_SET_BIT(__buf, __bit)	((uint8_t*)(__buf))[((__bit) >> 3)] |= (((uint8_t)1) << ((__bit) & 0x07))


