typedef struct ldpc_matrix_s {
	size_t cw_size; // n Size of codeword / columns count
	size_t cps_size; // Z Size of cyclic-permutation submatrices
	size_t rate_numerator;
	size_t rate_denominator; // the denominator of the rate
	size_t rows_count;
	size_t vals_count;
	const uint8_t *row_offset;
	const uint8_t *cols;
	const uint8_t *vals;
} ldpc_matrix_t, *ldpc_matrix_p;


#ifdef LDPC_FAST
typedef struct ldpc_matrix_decode_context_s {
	/* Constant part. */
	ldpc_matrix_p	matrix;
	size_t		n; /* Columns count. */
	size_t		n_float_size; /* Columns count * sizeof(float). */
	size_t		m; /* Rows count or sindrom_size in bits. */
	size_t		nm; /* n*m */
	size_t		msg_size; /* n in bytes */
	size_t		sindrom_size; /* m in bytes */
	size_t		data_size; /* Variable part data size. */
	void		*sptr; /* Variable part precalculated data. */
	void		*dptr; /* Variable part temporal data. */
	/* Variable part. */
	ldpc_float_p	r; /* vector, n size*/
	ldpc_float_p	e; /* matrix, nm size */
	/* Temporal. */
	ldpc_float_p	matr; /* nm size */
	size_t		*idxs; /* temp index array */
	uint8_t		*csindrom; /* calculated sindrom. */
} ldpc_decode_ctx_t, *ldpc_decode_ctx_p;
#endif

