static inline void
ldpc_cvt_bin(uint8_t *buf, size_t buf_size, uint8_t *bin) {
	static uint8_t *bin_tbl = (uint8_t*)"01";
	register uint8_t *buf_max, byte;

	for (buf_max = (buf + buf_size); buf < buf_max; buf ++) {
		byte = (*buf);
		(*bin ++) = bin_tbl[((byte >> 0) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 1) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 2) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 3) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 4) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 5) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 6) & 0x01)];
		(*bin ++) = bin_tbl[((byte >> 7) & 0x01)];
	}
	(*bin) = 0;
}

/* Import from little-endian hex string (L->H). */
static inline int
ldpc_import_le_bin(uint8_t *a, size_t count, uint8_t *buf, size_t buf_size) {
	register uint8_t *r_pos, *r_pos_max, *w_pos, *w_pos_max, cur_char, byte = 0;
	register size_t cnt;

	if (0 == count || 0 == buf_size)
		return (EINVAL);
	if ((count * sizeof(uint8_t)) < (buf_size / 8))
		return (EOVERFLOW);
	r_pos = buf;
	r_pos_max = (r_pos + buf_size);
	w_pos = (uint8_t*)a;
	w_pos_max = (w_pos + (count * sizeof(uint8_t)));

	for (cnt = 0; r_pos < r_pos_max; r_pos ++) {
		byte = (byte >> 1);
		cur_char = (*r_pos);
		if ('1' == cur_char) {
			byte |= (1 << 7);
		}
		cnt ++;
		if (8 > cnt) /* Wait untill 8 bit before write a byte. */
			continue;
		if (w_pos == w_pos_max)
			return (EOVERFLOW);
		(*w_pos ++) = byte;
		byte = 0;
		cnt = 0;
	}
	if (0 != cnt) { /* Last bits, less then byte. */
		byte = (byte >> (8 - cnt));
		if (w_pos == w_pos_max)
			return (EOVERFLOW);
		(*w_pos ++) = byte;
	}
	memset(w_pos, 0, (size_t)(w_pos_max - w_pos));
	return (0);
}


/* 0 - OK, non zero - error */
static inline int
ldpc_self_test() {
	int error = 0;
	size_t i, tm, msg_size, rcvd_msg_size;
	uint8_t	msg[LDPC_TEST_LEN];
	uint8_t	rcvd_msg[LDPC_TEST_LEN];
	uint8_t	cresult[LDPC_TEST_LEN];
	uint8_t	result[LDPC_TEST_LEN];
	uint8_t	tmpbuf[LDPC_TEST_LEN];

	/* Test 1: sindrom encode. */
	for (i = 0; 0 != ldpc_tst1v[i].msg_size; i ++) {

		if (0 != ldpc_import_le_bin(msg, sizeof(msg),
		    ldpc_tst1v[i].msg, ldpc_tst1v[i].msg_size)) {
			ldpc_print("test 1: ldpc_import_le_bin(ldpc_tst1v[%zu].msg) fail!\n", i);
			error ++;
			continue;
		}
		msg_size = LDPC_BITS2BYTES(ldpc_tst1v[i].msg_size);

		/* sindrom gen test. */
#if 0
		ldpc_cvt_bin(msg, msg_size, tmpbuf);
		ldpc_print("test 1: %zu: msg: %s\n", i, tmpbuf);
#endif
		/* 1/2 */
		if (0 != ldpc_import_le_bin(cresult, sizeof(cresult),
		    ldpc_tst1v[i].s_1_2, ldpc_tst1v[i].s_1_2_size)) {
			ldpc_print("test 1: 1/2 ldpc_import_le_bin(ldpc_tst1v[%zu].s_1_2) fail!\n", i);
			error ++;
			continue;
		}
		tm = LDPC_BITS2BYTES(ldpc_tst1v[i].s_1_2_size);
		if (0 != ldpc_encode(&ldpc_matrix_1944_1_2, msg, msg_size,
		    result, sizeof(result), &tm)) {
			ldpc_print("test 1: 1/2 %zu ldpc_encode: SKIPPED!\n", i);
		} else {
			if (0 != memcmp(cresult, result, tm)) {
				ldpc_cvt_bin(result, tm, tmpbuf);
				ldpc_print("test 1: 1/2 %zu ldpc_encode:      ERROR: \ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst1v[i].s_1_2, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 1: 1/2 %zu ldpc_encode:      OK!\n", i);
			}
		}

		/* 2/3 */
		if (0 != ldpc_import_le_bin(cresult, sizeof(cresult),
		    ldpc_tst1v[i].s_2_3, ldpc_tst1v[i].s_2_3_size)) {
			ldpc_print("test 1: 2/3 ldpc_import_le_bin(ldpc_tst1v[%zu].s_2_3) fail!\n", i);
			error ++;
			continue;
		}
		tm = LDPC_BITS2BYTES(ldpc_tst1v[i].s_2_3_size);
		if (0 != ldpc_encode(&ldpc_matrix_1944_2_3, msg, msg_size,
		    result, sizeof(result), &tm)) {
			ldpc_print("test 1: 2/3 %zu ldpc_encode: SKIPPED!\n", i);
		} else {
			if (0 != memcmp(cresult, result, tm)) {
				ldpc_cvt_bin(result, tm, tmpbuf);
				ldpc_print("test 1: 2/3 %zu ldpc_encode:      ERROR: \ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst1v[i].s_2_3, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 1: 2/3 %zu ldpc_encode:      OK!\n", i);
			}
		}

		/* 3/4 */
		if (0 != ldpc_import_le_bin(cresult, sizeof(cresult),
		    ldpc_tst1v[i].s_3_4, ldpc_tst1v[i].s_3_4_size)) {
			ldpc_print("test 1: 3/4 ldpc_import_le_bin(ldpc_tst1v[%zu].s_3_4) fail!\n", i);
			error ++;
			continue;
		}
		tm = LDPC_BITS2BYTES(ldpc_tst1v[i].s_3_4_size);
		if (0 != ldpc_encode(&ldpc_matrix_1944_3_4, msg, msg_size,
		    result, sizeof(result), &tm)) {
			ldpc_print("test 1: 3/4 %zu ldpc_encode: SKIPPED!\n", i);
		} else {
			if (0 != memcmp(cresult, result, tm)) {
				ldpc_cvt_bin(result, tm, tmpbuf);
				ldpc_print("test 1: 3/4 %zu ldpc_encode:      ERROR: \ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst1v[i].s_3_4, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 1: 3/4 %zu ldpc_encode:      OK!\n", i);
			}
		}

		/* 5/6 */
		if (0 != ldpc_import_le_bin(cresult, sizeof(cresult),
		    ldpc_tst1v[i].s_5_6, ldpc_tst1v[i].s_5_6_size)) {
			ldpc_print("test 1: 5/6 ldpc_import_le_bin(ldpc_tst1v[%zu].s_5_6) fail!\n", i);
			error ++;
			continue;
		}
		tm = LDPC_BITS2BYTES(ldpc_tst1v[i].s_5_6_size);
		if (0 != ldpc_encode(&ldpc_matrix_1944_5_6, msg, msg_size,
		    result, sizeof(result), &tm)) {
			ldpc_print("test 1: 5/6 %zu ldpc_encode: SKIPPED!\n", i);
		} else {
			if (0 != memcmp(cresult, result, tm)) {
				ldpc_cvt_bin(result, tm, tmpbuf);
				ldpc_print("test 1: 5/6 %zu ldpc_encode:      ERROR: \ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst1v[i].s_5_6, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 1: 5/6 %zu ldpc_encode:      OK!\n", i);
			}
		}
	}

	/* Test 2: sindrom decode. */
	for (i = 0; 0 != ldpc_tst2v[i].msg_size; i ++) {

		if (0 != ldpc_import_le_bin(msg, sizeof(msg),
		    ldpc_tst2v[i].msg, ldpc_tst2v[i].msg_size)) {
			ldpc_print("test 2: ldpc_import_le_bin(ldpc_tst2v[%zu].msg) fail!\n", i);
			error ++;
			continue;
		}
		msg_size = LDPC_BITS2BYTES(ldpc_tst2v[i].msg_size);

		if (0 != ldpc_import_le_bin(rcvd_msg, sizeof(rcvd_msg),
		    ldpc_tst2v[i].rcvd_msg, ldpc_tst2v[i].rcvd_msg_size)) {
			ldpc_print("test 2: ldpc_import_le_bin(ldpc_tst2v[%zu].rcvd_msg) fail!\n", i);
			error ++;
			continue;
		}
		rcvd_msg_size = LDPC_BITS2BYTES(ldpc_tst2v[i].rcvd_msg_size);

		/* sindrom decode test. */
#if 0
		ldpc_cvt_bin(msg, msg_size, tmpbuf);
		ldpc_print("test 2: %02zu: msg: %s\n", i, tmpbuf);
#endif
		if (0 != ldpc_import_le_bin(cresult, sizeof(cresult),
		    ldpc_tst2v[i].rcvd_sindrom, ldpc_tst2v[i].rcvd_sindrom_size)) {
			ldpc_print("test 2: ldpc_import_le_bin(ldpc_tst2v[%zu].rcvd_sindrom) fail!\n", i);
			error ++;
			continue;
		}
		tm = LDPC_BITS2BYTES(ldpc_tst2v[i].rcvd_sindrom_size);
		if (0 != ldpc_encode(ldpc_tst2v[i].ldpc_matrix,
		    rcvd_msg, rcvd_msg_size, result, sizeof(result), &tm)) {
			ldpc_print("test 2: %02zu ldpc_encode: SKIPPED!\n", i);
		} else {
			if (0 != memcmp(cresult, result, tm)) {
				ldpc_cvt_bin(result, tm, tmpbuf);
				ldpc_print("test 2: %02zu ldpc_encode: %zu/%zu  ERROR: \ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator,
				    ldpc_tst2v[i].rcvd_sindrom, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 2: %02zu ldpc_encode: %zu/%zu  OK!\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator);
			}
		}

		if (0 != ldpc_decode(ldpc_tst2v[i].ldpc_matrix, msg, msg_size,
		    cresult, LDPC_BITS2BYTES(ldpc_tst2v[i].rcvd_sindrom_size),
		    ldpc_tst2v[i].eps, ldpc_tst2v[i].max_rounds,
		    result, &tm)) {
			if (ldpc_tst2v[i].rounds) {
				ldpc_print("test 2: %02zu ldpc_decode: %zu/%zu  FAIL! - must be %zu rounds\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator,
				    ldpc_tst2v[i].rounds);
			} else {
				ldpc_print("test 2: %02zu ldpc_decode: %zu/%zu  OK - FAIL!\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator);
			}
		} else {
			if (0 != memcmp(result, rcvd_msg, msg_size)) {
				ldpc_cvt_bin(result, msg_size, tmpbuf);
				ldpc_print("test 2: %02zu ldpc_decode: %zu/%zu  ERROR: rounds: %zu/%zu\ncontrol:    %s\ncalculated: %s\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator,
				    tm, ldpc_tst2v[i].rounds,
				    ldpc_tst2v[i].rcvd_msg, tmpbuf);
				error ++;
			} else {
				ldpc_print("test 2: %02zu ldpc_decode: %zu/%zu  OK! rounds: %zu/%zu\n",
				    i, ldpc_tst2v[i].ldpc_matrix->rate_numerator,
				    ldpc_tst2v[i].ldpc_matrix->rate_denominator,
				    tm, ldpc_tst2v[i].rounds);
			}
		}
	}

	return (error);
}
